    var fs = require('fs');
    var async = require('async');

    var figlet = require('./lib/node-figlet');

    figlet.fonts(function(err, fonts) {
        if (err) {
            errCount++;
            return;
        }


        async.eachSeries(fonts, function(font, next) {
            figlet.text('abc ABC ...', {
                font: font
            }, function(err, data) {
                if (err) {
                    errCount++
                }
                next();
            });
        }, function(err) {
            console.log('complete!');
        });
    });
